package com.abbos.dockerexample;import com.abbos.dockerexample.service.UserService;import com.abbos.dockerexample.util.DataGenerator;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.CommandLineRunner;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.annotation.Bean;@SpringBootApplicationpublic class DockerExampleApplication {    private static final Logger logger = LoggerFactory.getLogger(DockerExampleApplication.class);    public static void main(String[] args) {        SpringApplication.run(DockerExampleApplication.class, args);    }    @Bean    public CommandLineRunner commandLineRunner(DataGenerator dataGenerator,                                               UserService userService) {        return args -> {            logger.info("starting...");            userService.saveAll(dataGenerator.generateData());            logger.info("done");        };    }}